$(document).ready(function() {

		var nFilas = $("#myTable tr").length;
		$("#adicionados").append(nFilas - 1);
		
			$('#adicionar').click(function() {	
				var nFilas = $("#myTable tr").length;
				if((nFilas-1) < 32){ //ARREGLAR, NO SIRVE

					if(verificar()){

				    var club = document.getElementById("club").value;
				    var spais = document.getElementById("pais");
				    var pais = spais.options[spais.selectedIndex].text;
				    var i = 1;
				    var fila = '<tr id="row' + i + '"><td class="codprod" id="tdclub">' + club + '</td><td id="tdpais">' + pais + '</td><td><button type="button" name="remove" id="' + i + '" class="btn btn-danger btn_remove">Quitar</button></td></tr>'; 

				    i++;

				    $('#myTable tr:first').after(fila);
				    $("#adicionados").text("Total Clubes Participando: "); 
				    var nFilas = $("#myTable tr").length;
				    $("#adicionados").append(nFilas - 1);
				    document.getElementById("club").value = "";
				    document.getElementById("pais").selectedIndex = 0;

					}
					
		  	  	}else{
		  	  	alert ("Limite de clubes alcanzado");
		  	  	}
		  
		  });

		$(document).on('click', '.btn_remove', function() {
		  var button_id = $(this).attr("id");
		    $('#row' + button_id + '').remove(); 
		    $("#adicionados").text("Total Clubes Participando: ");
		    var nFilas = $("#myTable tr").length;
		    $("#adicionados").append(nFilas - 1);
		  });
		
		});	